//IMPORTS

import "../styles/App.scss";
import adalabersList from "../data/adalabers.json";
import { useState } from "react";

//import { useState } from "react";

function App() {
  //CONSTANTES
  const [data, setData] = useState(adalabersList); //datos de las adalabers. Inicialmente el json.

  //FUNCIONES MANEJADORAS

  //función para pintar la lista de adalabers recorriendo el array adalabersList(data), con map

  const htmlAdalaber = data.map((eachAdalaber, index) => (
    <tr key={eachAdalaber.id}>
      <td>{eachAdalaber.name}</td>
      <td>{eachAdalaber.counselor}</td>
      <td>{eachAdalaber.speciality}</td>
    </tr>
  ));

  //RETURN

  return (
    <div className="page">
      {/* header */}
      <header>
        <h1>Adalabers</h1>
      </header>
      {/* main */}
      <main>
        <table>
          {/*Fila de cabecera */}
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Tutora</th>
              <th>Especialidad</th>
            </tr>
          </thead>
          {/*Fin fila de cabecera */}
          <tbody>
            {/*Primera fila */}
            {htmlAdalaber}
            {/*Segunda fila */}
            <tr>
              <td>Columa 1 de la fila 2</td>
              <td>Columa 2 de la fila 2</td>
              <td>Columa 3 de la fila 2</td>
            </tr>
          </tbody>
        </table>
      </main>
    </div>
  );
}

export default App;
