//IMPORTS

import "../styles/App.scss";
import adalabersList from "../data/adalabers.json";
import { useState } from "react";

//import { useState } from "react";

function App() {
  //CONSTANTES
  const [data, setData] = useState(adalabersList); //datos de las adalabers. Inicialmente el json.

  //FUNCIONES MANEJADORAS

  //función para pintar la lista de adalabers recorriendo el array adalabersList(data), con map

  const htmlAdalaber = data.results.map((eachAdalaber) => {
    return (
      <tr key={eachAdalaber.id}>
        <td>{eachAdalaber.name}</td>
        <td>{eachAdalaber.counselor}</td>
        <td>{eachAdalaber.speciality}</td>
      </tr>
    );
  });

  //funciones para añadir una nueva adalaber
  const handleNewName = () => {
    setName(ev.currentTarget.value);
  };
  const handleNewCounselor = () => {
    setCounselor(ev.currentTarget.value);
  };

  const handleNewSpeciality = () => {
    setSpeciality(ev.currentTarget.value);
  };
  //variables de estado para los inputs de crear nuevo contacto
  const [name, setName] = useState("");
  const [counselor, setCounselor] = useState("");
  const [speciality, setSpeciality] = useState("");

  //función manejadora del botón añadir una nueva Adalaber
  const handleClick = (ev) => {
    ev.preventDefault();
  };
  //RETURN

  return (
    <div className="page">
      {/* header */}
      <header>
        <h1>Adalabers</h1>
      </header>
      {/* main */}
      <main>
        <table>
          {/*Fila de cabecera */}
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Tutora</th>
              <th>Especialidad</th>
            </tr>
          </thead>

          {/*Fin fila de cabecera */}
          <tbody>
            {/*Primera fila */}
            {htmlAdalaber}
            {/*Segunda fila */}
          </tbody>
        </table>
        <form>
          <h2>Añadir una Adalaber</h2>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Nombre"
            onChange={handleNewName}
          ></input>
          <input
            type="text"
            name="counselor"
            id="counselor"
            placeholder="Tutora"
            onChange={handleNewCounselor}
          ></input>
          <input
            type="text"
            name="speciality"
            id="speciality"
            placeholder="Especialidad"
            onChange={handleNewSpeciality}
          ></input>
          <input
            type="submit"
            value="Añadir una nueva Adalaber"
            onClick={handleClick}
          ></input>
        </form>
      </main>
    </div>
  );
}

export default App;
